import './profile.css'
import avatarDefaul from '../../assets/avatar.png'
import {FiSettings, FiUpload} from 'react-icons/fi'
import Header from '../../components/Header'
import Title from '../../components/Title'
import { useState, useContext } from 'react'
import { AuthContext } from '../../contexts/auth'
import firabase from '../../services/firebaseConnection'



export default function Profile() {
  // Context
  const { user, deslogarFirebase } = useContext( AuthContext )
  // Estados
  const [nome, setNome] = useState(user && user.nome)
  const [email, setEmail] = useState(user && user.email)
  const [avatarUrl, setAvatarUrl] = useState( user && user.avatarUrl )
  // Para quando o usuario enviar a foto, salvar em um state separada
  const [imageEnvi, setImageEnvi] = useState(null)


  async function handleSave( e ) {
    e.preventDefault()

    // Para caso queira mudar sÃ³ o nome
    if ( imageEnvi === null && nome !=='') {
      await firabase.firestore().collection( 'users' )
      .doc( user.uid )
      .update( {

      })
 }
}

 return (
   <div>
     <Header />

     <div className='content'>
       <Title name='Meu perfil'>
         <FiSettings size={25}/>
       </Title>


       <div className='container'>
         <form className='form-profile' onSubmit={handleSave}>
           <label className='label-avatar'>
             <span><FiUpload color='#FFF' size={25} /></span>
             <input type='file' accept='image/*' /><br />
             {avatarUrl === null ?
               <img src={avatarDefaul} width='250' height='250' alt='Foto de perfil padrÃ£o' />
               : <img src={avatarUrl} width='250' height='250' alt='Foto de perfil padrÃ£o'/> 
            }
           </label>

           <label>Nome</label>
           <input type='text' value={nome} onChange={(e)=> setNome(e.target.value) }/>
           <label>E-mail</label>
           <input type='text' value={email} disabled={true} />
           <button type='submit'>Salvar</button>
        </form>
       </div>

       <div className='container'>
            <button className='logout-btn' onClick={()=> deslogarFirebase()}>
              Sair
            </button>
       </div>
     </div>

   </div>
 );
}
