import { useState } from 'react'
import firebase from '../../services/firebaseConnection'

import './customers.css'
import {toast} from 'react-toastify'
import {FiUser} from 'react-icons/fi'
import Title from '../../components/Title'
import Header from '../../components/Header'





export default function Customers() {
    // States PF- pessoa Fisica
    const [nome,setNome] = useState('')
    const [telCli,setTelCli] = useState('')
    const [enderecoCli, setEnderecoCli] = useState( '' )
    // Pessoa Juridica - CNPJ
    const [cnpj,setCpnj] = useState('')


  async  function handleCadastrarCliente( e ) {
        e.preventDefault()

    //Verificação dos campos do form
      if ( nome !== "" && telCli !== '' && enderecoCli !== '' && cnpj ==='' || cnpj !=='' ) {
            // Indo criar uma coleção no firabe
          await firebase.firestore().collection( 'clientes' )
            .add( {
                nome: nome,
                contato: telCli,
                endereco: enderecoCli,
                cnpj: cnpj
            } )
            // Se cadastra, o then só limpa os campos
            .then( () => {
                setNome( '' )
                setTelCli( '' )
                setEnderecoCli( '' )
                setCpnj( '' )
                toast.success('Cliente cadastrado com sucesso!')

          })
     }
    }



    return (
        <div>
            <Header/>

            <div className='content'>
                <Title name='Clientes'>
                    <FiUser size={25}/>
                </Title>

                <div className='container'>
                    <form className='form-profile customers' onSubmit={handleCadastrarCliente}>
                        <label>
                            Pessoal Física - CPF / Pessoal Juridica - CNPJ
                        </label><br/>
                        <label>Nome</label>
                        <input type='text'placeholder='Nome Cliente / Nome fanstasia' value={nome} onChange={(e)=> setNome(e.target.value) }/>

                        <label>Contato</label>
                        <input type='text'placeholder='Telefone-Fixo ou Celular' value={telCli} onChange={(e)=> setTelCli(e.target.value) }/>

                        <label>Endereço</label>
                        <input type='text' placeholder=' Cliente / Empresa' value={enderecoCli} onChange={( e ) => setEnderecoCli( e.target.value )} />
                        <label>CNPJ</label>
                        <input type='text' placeholder='CNPJ' value={cnpj} onChange={( e ) => setCpnj( e.target.value )} />
                        <button type='submit'>Cadastrar</button>
                    </form>
                </div>
            </div>
        </div>
    );
}
