import {useState, useEffect, useContext} from 'react';
import './novo.css'
import Header from '../../components/Header';
import Title from '../../components/Title';
import { FiPlusCircle } from 'react-icons/fi'
import { AuthContext } from '../../contexts/auth'
import firebase from '../../services/firebaseConnection'

export default function New() {
  // Contexto
const {user} = useContext(AuthContext)

  // Estados
const [servico, setServico] = useState('Avaliacao')
const [status, setStatus] = useState( 'Aberto' )
const [automovel, setAutomovel] = useState('')
const [clientes, setClientes] = useState( [] )
const [loadClientesState, setLoadClientesState] = useState( true )
const [clintesInputSelect, setClintesInputSelect] = useState(0)  

  useEffect( () => {
    async function loadClientes() {
      // Indo no firebase buscar a coleção de clientes
      await firebase.firestore().collection( 'clientes' )
      .get()
        .then( (snapshot) => {
          let lista = []
          snapshot.forEach( ( doc ) => {
            lista.push( {
              id: doc.id,
              nome: doc.data().nome
            })
          } )
        //Se não tiver nenhum cliente cadastradi no firebase
          if ( lista.length === 0 ) {
            console.log( 'Nenhum cliente encontrado' );
            setClientes( { id: '1', nome: '' } )
            setLoadClientesState( false )
            return;
          }
          setClientes( lista )
          setLoadClientesState(false)
      } )
        .catch( ( erro ) => {
          console.log(erro);
          setLoadClientesState( false )
          setClientes({id: '1', nome:''})
      })
    }

    loadClientes()

  },[])

  function cadastrarChamadoFirebase( e ) {
    e.preventDefault()
    alert("Clicou")
  }
  // Chamando quando troca servico
  function trocaOpcaoSelect(e){
    setServico( e.target.value )


  }
  // Chama quando troca status
  function trocaOpcaoRadio( e ) {
    setStatus( e.target.value )

  }
  //Chama quando troca cliente
  function handleMudarClientes(e) {

  }

 return (
   <div>
     <Header />

     <div className='content'>
       <Title name='Novo chamado'>
         <FiPlusCircle size={25}/>
       </Title>

       <div className='container'>
         <form className='form-profile chamado' onSubmit={cadastrarChamadoFirebase}>
           <label>Cliente</label>
           <select value={clintesInputSelect} onChange={handleMudarClientes}>
             {clientes.map( ( item, index ) => {
               return (
                 <option key={item.id} value={index}>
                   {item.nome}
                </option>
              )
            })}
           </select>
           <label>Serviço</label>
           <select value={servico} onChange={trocaOpcaoSelect}>
              <option value='Avaliacao'>Avaliação</option>
              <option value='Revisao'>Revisão</option>
              <option value='Troca de Oleo'>Troca de oleo/Filtro</option>          
              <option value='Alinhamento-Balanceamento'>Alinhamento/Balanceamento</option>
              <option value='Revisao Obrigatoria'>Revisão obrigatória</option>
              <option value='Manutencao Preventiva'>Manutenção preventiva</option>
              <option value='Manutencao Corretiva'>Manutenção corretiva</option>
              <option value='Remap'>Remap</option>
              <option value='Eletrica'>Eletrica</option>
           </select>


           <label>Status</label>
           <div className='status'>
             <input type='radio' name='radio' value='Aberto'
               onChange={trocaOpcaoRadio} checked={status === 'Aberto'}/>
             <span>Em Aberto</span>

             <input type='radio' name='radio' value='Progresso'
               onChange={trocaOpcaoRadio} checked={status === 'Progresso'} />
             <span>Em Progresso</span>

             <input type='radio' name='radio' value='Atendido'
               onChange={trocaOpcaoRadio} checked={status === 'Atendido'} />
             <span>Atendido</span>
           </div><br/>

           <label>Automóvel</label><br/>
           <textarea type='text' placeholder='Auto: Placa / marca ou modelo'
             value={automovel} onChange={(e)=> setAutomovel(e.target.value) }/>

           <button type='submit'>Cadastrar</button>
         </form>
        </div>
     </div>
   </div>
 );
}
