import {useState, useEffect, useContext} from 'react';
import './novo.css'
import {toast} from 'react-toastify'
import Header from '../../components/Header';
import Title from '../../components/Title';
import { FiPlusCircle } from 'react-icons/fi'
import { AuthContext } from '../../contexts/auth'
import firebase from '../../services/firebaseConnection'
import {useHistory, useParams} from 'react-router-dom' // paragar id passado em rota

export default function New() {
  // routes
const { id } = useParams()
const historia = useHistory()

// Contexto
const {user} = useContext(AuthContext)

  // Estados
const [servico, setServico] = useState('Avaliacao')
const [status, setStatus] = useState( 'Aberto' )
const [automovel, setAutomovel] = useState('')
const [placa, setPlaca] = useState( '' )
const [complemento, setComplemento] = useState('')  
const [clientes, setClientes] = useState( [] )
const [loadClientesState, setLoadClientesState] = useState( true )
const [clintesInputSelect, setClintesInputSelect] = useState(0)  

  useEffect( () => {
  // Carregando clientes cadastrados no firebase
    async function loadClientes() {
      // Indo no firebase buscar a coleção de clientes
      await firebase.firestore().collection( 'clientes' )
      .get()
        .then( (snapshot) => {
          let lista = []
          snapshot.forEach( ( doc ) => {
            lista.push( {
              id: doc.id,
              nome: doc.data().nome
            })
          } )
        //Se não tiver nenhum cliente cadastradi no firebase
          if ( lista.length === 0 ) {
            console.log( 'Nenhum cliente encontrado' );
            setClientes( { id: '1', nome: '' } )
            setLoadClientesState( false )
            return;
          }
          setClientes( lista )
          setLoadClientesState(false)

          // Se tiver tentando editar chamado
          if ( id ) {
            loadId(lista)
          }
      } )
        .catch( ( erro ) => {
          console.log(erro);
          setLoadClientesState( false )
          setClientes({id: '1', nome:''})
      })
    }

    loadClientes()

  },[])

  //Registrando novo chamado no firabase
 async function cadastrarChamadoFirebase( e ) {
    e.preventDefault()

   await firebase.firestore().collection( 'chamados' )
     .add( {
       criado: new Date(),
       cliente: clientes[clintesInputSelect].nome,
       clienteID: clientes[clintesInputSelect].id,
       servico: servico,
       status: status,
       automovel: automovel,
       placa: placa,
       complemento: complemento,
       userID: user.uid
     } )
     .then( () => {
       toast.success( 'Chamado criado com sucesso!' )
       setAutomovel( '' )
       setPlaca( '' )
       setComplemento('')
       setClintesInputSelect(0)
     } )
     .catch( ( erro ) => {
       toast.error('Erro a tentar cadastrar, tente de novo!')
     console.log(erro);
   })
  }
  // Chamando quando troca servico
  function trocaOpcaoSelect(e){
    setServico( e.target.value )


  }
  // Chama quando troca status
  function trocaOpcaoRadio( e ) {
    setStatus( e.target.value )

  }
  //Chama quando troca cliente
  function handleMudarClientes(e) {
    // console.log('Index do cliente selecionado:', e.target.value);
    // console.log( ' Cliente selecionado:', clientes[e.target.value] );
    setClintesInputSelect(e.target.value)
  }
  // chama quando quiser editar chamado
  async function loadId() {

  }

 return (
   <div>
     <Header />

     <div className='content'>
       <Title name='Novo chamado'>
         <FiPlusCircle size={25}/>
       </Title>

       <div className='container'>
         <form className='form-profile chamado' onSubmit={cadastrarChamadoFirebase}>
           <label>Cliente</label>
           {loadClientesState ? (
             <input type='text' disabled={true} value='Carregando clientes...'/>
           ) : (
            <select value={clintesInputSelect} onChange={handleMudarClientes}>
            {/* Entrando no estado de cliente para pecorrer as informação já que é um array
                Pegando o elemento e a posição */}
                {clientes.map( ( item, index ) => {
                    return (
                    <option key={item.id} value={index}>
                          {item.nome}
                     </option>
                    )
                 })}
               </select>
             )
           }

           <label>Serviço</label>
           <select value={servico} onChange={trocaOpcaoSelect}>
              <option value='Avaliacao'>Avaliação</option>
              <option value='Revisao'>Revisão</option>
              <option value='Troca de Oleo / Filtro'>Troca de óleo / Filtro</option>

              <option value='Alinhamento-Balanceamento'>Alinhamento/Balanceamento</option>
              <option value='Revisao Obrigatoria'>Revisão obrigatória</option>
              <option value='Manutencao Preventiva'>Manutenção preventiva</option>
              <option value='Manutencao Corretiva'>Manutenção corretiva</option>
             <option value='Remap'>Remap</option>
             <option value='Acerto de Motor'>Acerto de motor</option>

              <option value='Eletrica'>Eletrica</option>
           </select>


           <label>Status</label>
           <div className='status'>
             <input type='radio' name='radio' value='Aberto'
               onChange={trocaOpcaoRadio} checked={status === 'Aberto'}/>
             <span>Em Aberto</span>

             <input type='radio' name='radio' value='Progresso'
               onChange={trocaOpcaoRadio} checked={status === 'Progresso'} />
             <span>Em Progresso</span>

             <input type='radio' name='radio' value='Atendido'
               onChange={trocaOpcaoRadio} checked={status === 'Atendido'} />
             <span>Atendido</span>
           </div><br/>

           <label>Automóvel</label><br/>
           <textarea type='text' placeholder=' marca ou modelo'
             value={automovel} onChange={(e)=> setAutomovel(e.target.value) }/>
           <label>Placa</label><br />
           <input className='placa' type='text'
             placeholder='Placa' value={placa}
             onChange={( e ) => setPlaca( e.target.value )} />

           <label>Complemento</label>
           <textarea className='complemento' type='text' placeholder='Complemento'
             value={complemento} onChange={(e)=> setComplemento(e.target.value) }/>
           <button type='submit'>Cadastrar</button>
         </form>
        </div>
     </div>
   </div>
 );
}
