// Pagina de cadastro
import logo from '../../assets/car1.png'
import { useState, useContext } from 'react'
import {AuthContext} from '../../contexts/auth'
import {Link} from 'react-router-dom'

// Component
function SignUp() {

  const [nome, setNome] = useState( '' )
  const [email, setEmail] = useState( '' )
  const [password, setPassword] = useState( '' )

  // Contexto
  const {cadastroFirebase} = useContext(AuthContext)

//  Funcção do form onSubmit
  function handleSubmit( e ) {
    e.preventDefault() // impede de recarregar a pagina
    alert('Clicou')
    // Verificando as States
  if ( nome !== "" && email !== "" && password !== '' ) {
      cadastroFirebase(email, password, nome)
    } 


}

 return (
   <div className='container-center'>
     <div className='login'>
       <div className='logo-area'>
         <img src={logo} alt='logo do sistema'/>
       </div>


       <form onSubmit={handleSubmit}>
         <h1>Cadastrar</h1>
         <input type='text' placeholder='Seu nome' value={nome}
         onChange={(e)=> setNome(e.target.value)}></input>
         <input type='text' placeholder='email@email.com'
           value={email} onChange={(e)=> setEmail(e.target.value)}></input>
         <input type='password' placeholder='senha'
           value={password} onChange={(e)=> setPassword(e.target.value)}></input>
         <button type='submit'>Cadastrar</button>
       </form>

       <Link to='/'>Já tem uma conta ?  Entre.</Link>
      </div >
   </div>
 );
}

export default SignUp;
