    const {user} = useContext(AuthContext) // user contem informações de usuario em um objeto


 return (
   <div className='sidebar'>
         <div>
             <img src={user.avatarUrl === null ? avatar : user.avatarUrl}/>
         </div>
   </div>
 );
}
